// import logo from './logo.svg';
import { useState } from 'react';
import './App.css';
import { PackingList } from './PackingList';

export function App() {
  const [items, setItems] = useState([]);

  function handleAddItems(item) {
    // React NOT allowing mutation
    setItems(items => [...items, item]);
  }

  function handleDeleteItem(id) {
    setItems(items => items.filter(item => item.id !== id));
  }

  function handleToggleItem(id) {
    setItems(items => items.map(item => item.id === id ? { ...item, packed: !item.packed } : item));
  }

  function handleClearList() {
    const confirmed = window.confirm('Are you sure you want to delete all items?');

    if (confirmed) setItems([]);
  }

  return (
    <div className="app">
      <Logo />
      <Form onAddItems={handleAddItems} />
      <PackingList items={items} onDeleteItem={handleDeleteItem} onToggleItem={handleToggleItem} onClearList={handleClearList} />
      <Stats items={items} />
    </div>
  );
}

export function Item({ item, onDeleteItem, onToggleItem }) {
  return (
    <li>
      <input type='checkbox' value={item.packed} onChange={() => onToggleItem(item.id)} />
      <span style={item.packed ? { textDecoration: 'line-through' } : {}}>{item.quantity} {item.description}</span>
      <button onClick={() => onDeleteItem(item.id)}>‚ùå</button>
    </li>
  );
}

export function Stats({ items }) {
  if (!items.length) {
    return (
      <p className='stats'>
        <em>
          üóøüóøüóøAdd items to your packing list üóøüóøüóø
        </em>
      </p>
    )
  }

  const numItems = items.length;
  const numPacked = items.filter(item => item.packed).length;
  const percentage = (isNaN(numItems) || isNaN(numPacked)) ? 0 : Math.round((numPacked / numItems) * 100);

  return (
    <footer className='stats'>
      <em>
        {percentage === 100 ? "You're ready to GO!!! ‚úàÔ∏è"
          : `You have ${numItems} items on your list, and you already packed ${numPacked} (${percentage}%)`}
      </em>
    </footer>
  );
}

export default App;
